mapper.select:
SELECT p.id as "productId", p.name as "productName", o.id as "offerId", op.amount as "price", op.currency_code as "currency", op.price_type as "priceType", s.seller_name as "sellerName", p.primary_image_url as "imageUrl", COALESCE(ra.average_rating, 0) as "averageRating", COALESCE(ra.total_reviews, 0) as "totalReviews" FROM hm_offer o JOIN hm_product p ON o.product_id = p.id JOIN hm_offer_price op ON op.offer_id = o.id JOIN hm_seller_store s ON s.id = p.seller_id LEFT JOIN hm_review_aggregation_snapshot ra ON ra.product_id = p.id WHERE p.status = 'ACTIVE' AND o.state_id = 'ACTIVE' AND op.price_type IN ('SALE', 'PROMOTION') AND (op.start_date IS NULL OR op.start_date <= CURRENT_TIMESTAMP) AND (op.end_date IS NULL OR op.end_date >= CURRENT_TIMESTAMP)
AND LOWER(p.name) LIKE LOWER(CONCAT('%', #{name}, '%'))
AND LOWER(s.seller_name) LIKE LOWER(CONCAT('%', #{sellerName}, '%'))
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY op.amount ASC
LIMIT #{numRowsInPage} OFFSET #{startRow}
