mapper.select:
SELECT id as "id", created_time as "createdTime", activity_name as "activityName", activity_success as "activitySuccess", CAST(activity_comment AS VARCHAR) as "activityComment" FROM hm_platform_activity_log WHERE 1=1
AND activity_name LIKE CONCAT('%', #{activityName}, '%')
AND activity_success = #{activitySuccess}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*) FROM hm_platform_activity_log WHERE 1=1
AND activity_name LIKE CONCAT('%', #{activityName}, '%')
AND activity_success = #{activitySuccess}
