mapper.select:
SELECT id as "id", occurred_at as "occurredAt", event_type as "eventType", platform_id as "platformId", actor_id as "actorId", CAST(payload_json AS VARCHAR) as "payloadJson" FROM hm_platform_audit_log WHERE 1=1
AND event_type = #{eventType}
AND actor_id LIKE CONCAT('%', #{actorId}, '%')
AND platform_id = #{platformId}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY occurred_at DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_platform_audit_log
WHERE 1=1

    AND event_type = #{eventType}


    AND actor_id LIKE CONCAT('%', #{actorId}, '%')


    AND platform_id = #{platformId}
