mapper.select:
SELECT id as "id", name as "name", state_id as "state", suspended as "suspended", suspension_reason as "suspensionReason", deleted as "deleted", deleted_at as "deletedAt", active_commission_policy_id as "activeCommissionPolicyId", active_feature_config_id as "activeFeatureConfigId" FROM platform_owner WHERE 1=1
AND LOWER(name) LIKE LOWER(CONCAT('%', #{name}, '%'))
AND state_id = #{state}
AND suspended = #{suspended}
AND deleted = #{deleted}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY name ASC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM platform_owner
WHERE 1=1

    AND LOWER(name) LIKE LOWER(CONCAT('%', #{name}, '%'))


    AND state_id = #{state}


    AND suspended = #{suspended}


    AND deleted = #{deleted}
