mapper.select:
SELECT id as "id", platform_id as "platformId", email as "email", business_name as "businessName", verified as "verified", kyc_completed as "kycCompleted", suspended as "suspended", suspension_reason as "suspensionReason", deleted as "deleted", can_create_listings as "canCreateListings", state_id as "state", flow_id as "flowId", onboarding_policy_id as "onboardingPolicyId", onboarding_policy_version as "onboardingPolicyVersion", policy_locked_at as "policyLockedAt", created_time as "createdAt", last_modified_time as "updatedAt" FROM hm_seller_account WHERE 1=1
AND id = #{sellerId}
AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))
AND LOWER(email) LIKE LOWER(CONCAT('%', #{email}, '%'))
AND platform_id = #{platformId}
AND verified = #{verified}
AND kyc_completed = #{kycCompleted}
AND suspended = #{suspended}
AND deleted = #{deleted}
AND state_id = #{state}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_seller_account
WHERE 1=1

    AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))


    AND LOWER(email) LIKE LOWER(CONCAT('%', #{email}, '%'))


    AND platform_id = #{platformId}


    AND verified = #{verified}


    AND kyc_completed = #{kycCompleted}


    AND suspended = #{suspended}


    AND deleted = #{deleted}


    AND state_id = #{state}
