mapper.select:
SELECT id as "id", policy_key as "policyKey", policy_name as "policyName", CAST(description AS VARCHAR) as "description", default_effect as "defaultEffect", owner_service as "ownerService", is_active as "isActive", flow_id as "flowId", state_id as "stateId", created_by as "createdBy", created_time as "createdAt", last_modified_time as "updatedAt" FROM hm_policy_definition WHERE 1=1
AND policy_key = #{policyKey}
AND LOWER(policy_name) LIKE LOWER(CONCAT('%', #{policyName}, '%'))
AND owner_service = #{ownerService}
AND is_active = #{isActive}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_policy_definition
WHERE 1=1

    AND policy_key = #{policyKey}


    AND LOWER(policy_name) LIKE LOWER(CONCAT('%', #{policyName}, '%'))


    AND owner_service = #{ownerService}


    AND is_active = #{isActive}
