mapper.select:
SELECT id, platform_id as platformId, email, business_name as businessName, state_id as currentState, flow_id as flow, verified, kyc_completed as kycCompleted, onboarding_policy_id as onboardingPolicyId, onboarding_policy_version as onboardingPolicyVersion, policy_locked_at as policyLockedAt, created_at as createdAt, updated_at as updatedAt FROM seller_account WHERE 1=1 AND deleted = false
AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))
AND platform_id = #{platformId}
AND state_id = #{currentState}
AND flow_id = #{flow}
AND verified = #{verified}
AND kyc_completed = #{kycCompleted}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_at DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM seller_account
WHERE 1=1
AND deleted = false

    AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))


    AND platform_id = #{platformId}


    AND state_id = #{currentState}


    AND flow_id = #{flow}


    AND verified = #{verified}


    AND kyc_completed = #{kycCompleted}
