mapper.select:
SELECT id, platform_id as platformId, email, business_name as businessName, verified, kyc_completed as kycCompleted, suspended, suspension_reason as suspensionReason, deleted, can_create_listings as canCreateListings, state_id as state, flow_id as flow, onboarding_policy_id as onboardingPolicyId, onboarding_policy_version as onboardingPolicyVersion, policy_locked_at as policyLockedAt, created_at as createdAt, updated_at as updatedAt FROM seller_account WHERE 1=1
AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))
AND LOWER(email) LIKE LOWER(CONCAT('%', #{email}, '%'))
AND platform_id = #{platformId}
AND verified = #{verified}
AND kyc_completed = #{kycCompleted}
AND suspended = #{suspended}
AND deleted = #{deleted}
AND state_id = #{state}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_at DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM seller_account
WHERE 1=1

    AND LOWER(business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))


    AND LOWER(email) LIKE LOWER(CONCAT('%', #{email}, '%'))


    AND platform_id = #{platformId}


    AND verified = #{verified}


    AND kyc_completed = #{kycCompleted}


    AND suspended = #{suspended}


    AND deleted = #{deleted}


    AND state_id = #{state}
