mapper.select:
SELECT id as "id", rule_name as "ruleName", description as "description", rule_key as "ruleKey", state_id as "stateId", flow_id as "flowId", start_time as "effectiveFrom", end_time as "effectiveTo", created_time as "createdTime" FROM hm_pricing_rule_definition WHERE rule_type = 'COMMISSION' AND tenant = #{tenantId}
AND LOWER(rule_name) LIKE LOWER(CONCAT('%', #{ruleName}, '%'))
AND state_id = #{stateId}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_pricing_rule_definition
WHERE rule_type = 'COMMISSION'
  AND tenant = #{tenantId}

    AND LOWER(rule_name) LIKE LOWER(CONCAT('%', #{ruleName}, '%'))


    AND state_id = #{stateId}
