mapper.select:
SELECT 
    id as "id",
    from_currency as "baseCurrency",
    to_currency as "targetCurrency",
    exchange_rate as "rate",
    effective_date as "effectiveFrom",
    'MANUAL' as "source"
FROM hm_currency_conversion
FROM hm_currency_conversion
WHERE tenant = #{tenantId}

    AND from_currency = #{baseCurrency}


    AND to_currency = #{targetCurrency}


    ORDER BY ${sortColumn} ${sortOrder}


    ORDER BY effective_date DESC


    LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_currency_conversion
FROM hm_currency_conversion
WHERE tenant = #{tenantId}

    AND from_currency = #{baseCurrency}


    AND to_currency = #{targetCurrency}
