mapper.select:
SELECT 
    id as "id",
    dispute_number as "disputeNumber",
    order_id as "orderId",
    seller_id as "sellerId",
    customer_id as "customerId",
    dispute_type as "disputeType",
    dispute_reason as "disputeReason",
    dispute_amount as "disputeAmount",
    currency_code as "currencyCode",
    state_id as "stateId",
    flow_id as "flowId",
    created_time as "createdTime",
    last_modified_time as "updatedTime"
FROM hm_dispute
FROM hm_dispute
WHERE tenant = #{tenantId}

    AND order_id = #{orderId}


    AND seller_id = #{sellerId}


    AND state_id = #{stateId}


    AND dispute_type = #{disputeType}


    AND dispute_number = #{disputeNumber}


    ORDER BY ${sortColumn} ${sortOrder}


    ORDER BY created_time DESC


    LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_dispute
FROM hm_dispute
WHERE tenant = #{tenantId}

    AND order_id = #{orderId}


    AND seller_id = #{sellerId}


    AND state_id = #{stateId}


    AND dispute_type = #{disputeType}


    AND dispute_number = #{disputeNumber}
