mapper.select:
SELECT k.id as "id", k.seller_id as "sellerId", s.business_name as "businessName", s.email as "email", k.document_type as "documentType", k.document_number as "documentNumber", k.s3_key as "s3Key", k.state_id as "stateId", k.flow_id as "flowId", k.created_time as "createdTime" FROM hm_seller_kyc k LEFT JOIN hm_seller_account s ON k.seller_id = s.id WHERE k.tenant = #{tenantId}
AND k.seller_id = #{sellerId}
AND k.state_id = #{stateId}
AND LOWER(s.business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY k.created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_seller_kyc k
LEFT JOIN hm_seller_account s ON k.seller_id = s.id
WHERE k.tenant = #{tenantId}

    AND k.seller_id = #{sellerId}


    AND k.state_id = #{stateId}


    AND LOWER(s.business_name) LIKE LOWER(CONCAT('%', #{businessName}, '%'))
