mapper.select:
SELECT id as "id", policy_version as "policyVersion", applicable_region as "countryCode", policy_code as "sellerType", state_id as "status", state_id as "stateId", flow_id as "flowId", effective_date as "effectiveDate", created_by as "createdBy", created_time as "createdAt", approved_by as "approvedBy", approval_date as "approvedAt" FROM hm_compliance_policy WHERE policy_type = 'ONBOARDING' AND tenant = #{tenantId}
AND policy_version = #{policyVersion}
AND state_id = #{status}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY created_time DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_compliance_policy
WHERE policy_type = 'ONBOARDING'
  AND tenant = #{tenantId}

    AND policy_version = #{policyVersion}


    AND state_id = #{status}
