mapper.select:
SELECT id as "id", name as "name", platform_code as "platformCode", country_code as "countryCode", currency_code as "currencyCode", marketplace_type as "marketplaceType", is_active as "isActive", flow_id as "flowId", state_id as "stateId", logo_url as "logoUrl", primary_color as "primaryColor", secondary_color as "secondaryColor", legal_name as "legalName", tax_id as "taxId", registration_number as "registrationNumber", address as "address", email as "email", phone as "phone", max_sellers as "maxSellers", max_orders as "maxOrders", max_marketplaces as "maxMarketplaces", activated_at as "activatedAt", created_time as "createdTime", created_by as "createdBy" FROM hm_platform FROM hm_platform WHERE tenant = #{tenantId}
AND id = #{id}
AND LOWER(name) LIKE LOWER(CONCAT('%', #{name}, '%'))
AND platform_code = #{platformCode}
AND country_code = #{countryCode}
AND is_active = #{isActive}
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY name ASC
LIMIT #{numRowsInPage} OFFSET #{startRow}

SELECT COUNT(*)
FROM hm_platform
FROM hm_platform
WHERE tenant = #{tenantId}

    AND id = #{id}


    AND LOWER(name) LIKE LOWER(CONCAT('%', #{name}, '%'))


    AND platform_code = #{platformCode}


    AND country_code = #{countryCode}


    AND is_active = #{isActive}
