mapper.select:
SELECT p.id as "productId", p.name as "productName", ps.trending_score as "trendingScore", ps.views_count as "viewsCount", MIN(op.sale_price) as "minPrice", MAX(op.currency_code) as "currency", p.primary_image_url as "imageUrl", s.seller_name as "sellerName", COALESCE(ra.average_rating, 0) as "averageRating", COALESCE(ra.total_reviews, 0) as "totalReviews" FROM hm_product p JOIN hm_product_popularity_signal ps ON ps.product_id = p.id JOIN hm_offer o ON o.product_id = p.id JOIN hm_offer_price op ON op.offer_id = o.id JOIN hm_seller_store s ON s.seller_account_id = p.seller_id LEFT JOIN hm_review_aggregation_snapshot ra ON ra.product_id = p.id WHERE p.tenant = #{tenantId} AND p.state_id = 'ACTIVE' AND o.state_id = 'ACTIVE' AND (op.sale_end_date IS NULL OR op.sale_end_date >= CURRENT_TIMESTAMP)
AND LOWER(p.name) LIKE LOWER(CONCAT('%', #{name}, '%'))
GROUP BY p.id, p.name, ps.trending_score, ps.views_count, p.primary_image_url, s.seller_name, ra.average_rating, ra.total_reviews
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY ps.trending_score DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}
