mapper.select:
SELECT p.id as "productId", p.name as "productName", pr.score as "relevanceScore", MIN(op.sale_price) as "minPrice", MAX(op.currency_code) as "currency", p.primary_image_url as "imageUrl", p.state_id as "state_id", p.flow_id as "flow_id", s.seller_name as "sellerName", COALESCE(ra.average_rating, 0) as "averageRating", COALESCE(ra.total_reviews, 0) as "totalReviews" FROM hm_product p JOIN hm_recommendation pr ON pr.target_product_id = p.id JOIN hm_offer o ON o.product_id = p.id JOIN hm_offer_price op ON op.offer_id = o.id JOIN hm_seller_store s ON s.seller_account_id = p.seller_id LEFT JOIN hm_review_aggregation_snapshot ra ON ra.product_id = p.id WHERE p.tenant = #{tenantId} AND p.state_id = 'ACTIVE' AND o.state_id = 'ACTIVE' AND (op.sale_end_date IS NULL OR op.sale_end_date >= CURRENT_TIMESTAMP)
AND pr.source_product_id = #{sourceProductId}
GROUP BY p.id, p.name, pr.score, p.primary_image_url, p.state_id, p.flow_id, s.seller_name, ra.average_rating, ra.total_reviews
ORDER BY ${sortColumn} ${sortOrder}
ORDER BY pr.score DESC
LIMIT #{numRowsInPage} OFFSET #{startRow}
