mapper.select:
SELECT id, first_name, last_name, email, phone_number, company_name AS company_name, date_of_birth AS date_of_birth, gender, is_individual AS indivisual, tenant FROM hm_customer
tenant = #{tenant_id}
AND ( UPPER(TRIM(first_name)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(last_name)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(phone_number)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(email)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(company_name)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) )
${orderby} ${pagination}

SELECT COUNT(*) FROM hm_customer
tenant = #{tenant_id}
AND ( UPPER(TRIM(first_name)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(last_name)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(phone_number)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(email)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) OR UPPER(TRIM(email)) LIKE UPPER(TRIM(CONCAT('%', #{searchString}, '%'))) )

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END as exists
FROM hm_customer
WHERE id = #{customer_id}
AND email = #{email}
